src dao ItemDAO package dao import entity.Item import util.DBHelp object ItemDAO { @JvmStatic fun getAllItems() = mutableListOf&lt;Item&gt;().apply { 从数据库中获取所有商品 DBHelp.connection .prepareStatement(&#34;SELECT * FROM item;&#34;) .executeQuery() .use { while (it.next()) { 遍历 ResultSet 并实例化 Item 实体 val item = Item( id = it.getInt(&#34;id&#34;), name = it.getString(&#34;name&#34;), city = it.getString(&#34;city&#34;), price = it.getInt(&#34;price&#34;), number = it.getInt(&#34;number&#34;), picture = it.getString(&#34;picture&#34;) ) this.add(item) } } } @JvmStatic fun getVisitedItems(s: String) = mutableListOf&lt;Item&gt;().apply { 根据 Cookie 从数据库中获取所有历史游览商品 s .split(&#39;|&#39;) String ➜ List&lt;String&gt; .reversed() 逆转 .map(String::toInt) String ➜ Int .map { getItemById(it) } Int ➜ Item .forEach { this.add(it) } } @JvmStatic fun getItemById(id: Int) = DBHelp.connection 根据 ID 从数据库中获取商品 .prepareStatement(&#34;SELECT * FROM item WHERE id = ?;&#34;).apply { this.setInt(1, id) } .executeQuery() .use { if (it.next()) { ID 存在 val item = Item( id = it.getInt(&#34;id&#34;), name = it.getString(&#34;name&#34;), city = it.getString(&#34;city&#34;), price = it.getInt(&#34;price&#34;), number = it.getInt(&#34;number&#34;), picture = it.getString(&#34;picture&#34;) ) item } else { ID 不存在 val item = Item( id = 0, name = &#34;null&#34;, city = &#34;null&#34;, price = 0, number = 0, picture = &#34;null&#34; ) item } } } entity Item package entity data class Item( 数据类：商品实体 val id: Int, val name: String, val city: String, val price: Int, val number: Int, val picture: String ) util DBHelp package util import java.sql.Connection import java.sql.DriverManager object DBHelp { @JvmStatic val connection: Connection by lazy { 获得数据库连接 Class.forName(&#34;org.postgresql.Driver&#34;) DriverManager.getConnection(&#34;jdbc:postgresql://127.0.0.1:5432/postgres&#34;, &#34;123&#34;, &#34;123456&#34;) } } CookieHelp package util object CookieHelp { @JvmStatic fun addCookie(cookie: String, id: Int) = cookie 添加 Cookie 并去重 .split(&#39;|&#39;) 分解（String ➜ List） .toMutableList().apply { this.add(id.toString()) } 添加 .distinct() 去重 .fold(&#34;&#34;) { str, s -&gt; if (str == &#34;&#34;) s else str + &#39;|&#39; + s } 合并（List ➜ String） @JvmStatic fun cutCookie(cookie: String) = if (cookie.split(&#39;|&#39;).count() &gt; 5) cookie.substring(cookie.indexOf(&#39;|&#39;) + 1) else cookie 字符串中只保留最近 5 个商品 ID } web index.jsp &lt;%@ page import=&#34;dao.ItemDAO&#34; %&gt; &lt;%@ page import=&#34;entity.Item&#34; %&gt; &lt;%@ page import=&#34;java.util.List&#34; %&gt; &lt;%@ page contentType=&#34;text/html;charset=UTF-8&#34; language=&#34;java&#34; %&gt; &lt;html&gt; &lt;head&gt; &lt;title&gt;商品列表&lt;/title&gt; &lt;style type=&#34;text/css&#34;&gt; div { float: left; margin: 10px; } div dd { margin: 0; font-size: 10pt; } &lt;/style&gt; &lt;/head&gt; &lt;body&gt; &lt;h1&gt;商品列表&lt;/h1&gt; &lt;hr&gt; &lt;center&gt; &lt;table width=&#34;700&#34; cellpadding=&#34;0&#34; cellspacing=&#34;0&#34; border=&#34;0&#34;&gt; &lt;tr&gt; &lt;td&gt; &lt;% final List&lt;Item&gt; list = ItemDAO.getAllItems(); 从数据库中获取所有商品 for (Item item : list) { 开始循环 %&gt; &lt;div&gt; &lt;dl&gt; &lt;dt&gt; &lt;a href=&#34;details.jsp?id=&lt;%=item.getId()%&gt;&#34;&gt;&lt;img src=&#34;images/&lt;%=item.getPicture()%&gt;&#34; width=&#34;120&#34; height=&#34;90&#34; border=&#34;1&#34;/&gt;&lt;/a&gt; &lt;/dt&gt; &lt;dd&gt;&lt;%=item.getName() %&gt; &lt;/dd&gt; &lt;dd&gt;产地:&lt;%=item.getCity()%&gt;&amp;nbsp;&amp;nbsp;价格:￥ &lt;%=item.getPrice()%&gt; &lt;/dd&gt; &lt;/dl&gt; &lt;/div&gt; &lt;% } 结束循环 %&gt; &lt;/td&gt; &lt;/tr&gt; &lt;/table&gt; &lt;/center&gt; &lt;/body&gt; &lt;/html&gt; details.jsp &lt;%@ page import=&#34;dao.ItemDAO&#34; %&gt; &lt;%@ page import=&#34;entity.Item&#34; %&gt; &lt;%@ page import=&#34;util.CookieHelp&#34; %&gt; &lt;%@ page import=&#34;java.util.List&#34; %&gt; &lt;%@ page contentType=&#34;text/html;charset=UTF-8&#34; language=&#34;java&#34; %&gt; 指定输出字符集 &lt;html&gt; &lt;head&gt; &lt;title&gt;商品详情&lt;/title&gt; &lt;style type=&#34;text/css&#34;&gt; div { float: left; margin: 5px 30px; } div dd { margin: 0; font-size: 10pt; } &lt;/style&gt; &lt;/head&gt; &lt;body&gt; &lt;h1&gt;商品详情&lt;/h1&gt; &lt;hr&gt; &lt;% request.setCharacterEncoding(&#34;UTF-8&#34;); 指定输入字符集 %&gt; &lt;% final int id = Integer.valueOf(request.getParameter(&#34;id&#34;)); 获得当前页面的商品 ID final Item item = ItemDAO.getItemById(id); 实例化 Item 实体 %&gt; &lt;% boolean hasCookie = false; final Cookie[] cookies = request.getCookies(); for (Cookie c : cookies) { if (c.getName().equals(&#34;visitedItem&#34;)) { 处理商品游览历史 Cookie hasCookie = true; String s1 = CookieHelp.addCookie(c.getValue(), id); 添加商品 ID 并去重 String s2 = CookieHelp.cutCookie(s1); Cookie 只保留最近 5 个商品 ID c.setValue(s2); response.addCookie(c); 添加 Cookie break; } } if (!hasCookie) { Cookie c = new Cookie(&#34;visitedItem&#34;, String.valueOf(id)); 不存在则新建 visitedItem Cookie response.addCookie(c); 添加 Cookie } %&gt; &lt;center&gt; &lt;table width=&#34;700&#34; cellpadding=&#34;0&#34; cellspacing=&#34;0&#34; border=&#34;0&#34;&gt; &lt;tr&gt; &lt;td width=&#34;70%&#34; valign=&#34;top&#34;&gt; &lt;table&gt; &lt;tr&gt; &lt;td rowspan=&#34;4&#34;&gt;&lt;img src=&#34;images/&lt;%=item.getPicture()%&gt;&#34; width=&#34;200&#34; height=&#34;160&#34;/&gt;&lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td&gt;&lt;B&gt;&lt;%=item.getName()%&gt; &lt;/B&gt;&lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td&gt;产地：&lt;%=item.getCity()%&gt; &lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td&gt;价格：&lt;%=item.getPrice()%&gt;￥&lt;/td&gt; &lt;/tr&gt; &lt;/table&gt; &lt;/td&gt; &lt;td width=&#34;30%&#34; bgcolor=&#34;#EEE&#34; align=&#34;center&#34;&gt; &lt;br&gt; &lt;b&gt;您最近浏览的商品&lt;/b&gt;&lt;br&gt; &lt;% for (Cookie c : request.getCookies()) { if (c.getName().equals(&#34;visitedItem&#34;)) { List&lt;Item&gt; list = ItemDAO.getVisitedItems(c.getValue()); 根据 Cookie 从数据库中获取所有历史游览商品 for (Item i : list) { 开始循环 %&gt; &lt;div&gt; &lt;dl&gt; &lt;dt&gt; &lt;a href=&#34;details.jsp?id=&lt;%=i.getId()%&gt;&#34;&gt;&lt;img src=&#34;images/&lt;%=i.getPicture()%&gt;&#34; width=&#34;120&#34; height=&#34;90&#34; border=&#34;1&#34;/&gt;&lt;/a&gt; &lt;/dt&gt; &lt;dd&gt;&lt;%=i.getName()%&gt; &lt;/dd&gt; &lt;dd&gt;产地:&lt;%=i.getCity()%&gt;&amp;nbsp;&amp;nbsp;价格:&lt;%=i.getPrice()%&gt; ￥&lt;/dd&gt; &lt;/dl&gt; &lt;/div&gt; &lt;% } 结束循环 break; } } %&gt; &lt;/td&gt; &lt;/tr&gt; &lt;/table&gt; &lt;/center&gt; &lt;/body&gt; &lt;/html&gt; sql CREATE TABLE item ( id SERIAL, name TEXT NOT NULL, city TEXT NOT NULL, price INTEGER NOT NULL, number INTEGER NOT NULL, picture TEXT NOT NULL, PRIMARY KEY (id) ); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;沃特篮球鞋&#39;, &#39;佛山&#39;, &#39;180&#39;, &#39;500&#39;, &#39;001.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;安踏运动鞋&#39;, &#39;福州&#39;, &#39;120&#39;, &#39;800&#39;, &#39;002.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;耐克运动鞋&#39;, &#39;广州&#39;, &#39;500&#39;, &#39;1000&#39;, &#39;003.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;阿迪达斯T血衫&#39;, &#39;上海&#39;, &#39;388&#39;, &#39;600&#39;, &#39;004.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;李宁文化衫&#39;, &#39;广州&#39;, &#39;180&#39;, &#39;900&#39;, &#39;005.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;小米3&#39;, &#39;北京&#39;, &#39;1999&#39;, &#39;3000&#39;, &#39;006.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;小米2S&#39;, &#39;北京&#39;, &#39;1299&#39;, &#39;1000&#39;, &#39;007.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;thinkpad笔记本&#39;, &#39;北京&#39;, &#39;6999&#39;, &#39;500&#39;, &#39;008.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;dell笔记本&#39;, &#39;北京&#39;, &#39;3999&#39;, &#39;500&#39;, &#39;009.jpg&#39;); INSERT INTO item (name, city, price, number, picture) VALUES (&#39;ipad5&#39;, &#39;北京&#39